mapper.select:
select
bid_id,i.user_id,u.nick_name,bid_name,bid_number,bid_deadline,bid_date,bid_place,winning_date,bid_opening_date,
tender_agency,bidding_unit,quoted_price,construction_site,bid_bond,win_bid,bid_bond_recovered,bid_bond_takeback_date,
bid_bond_reason_for_failure,perform_bond,perform_bond_recovered,perform_bond_takeback_date,perform_bond_reason_for_failure,bid_attachment,
i.instance_id,i.apply_time,project_id,project_category,project_manager,project_manager_name,project_manager_contact,i.agent,u2.nick_name
as agent_name,
agent_name,agent_contact,project_type,scale,business_category,union_company_name,i.apply_status
from pl_bid_info as i inner join sys_user as u on u.user_id = i.user_id inner join sys_user as u2 on i.agent =
u2.user_id

    and user_id = #{userId}
    and bid_name like concat('%', #{bidName}, '%')
    and bid_number = #{bidNumber}
    and bid_deadline = #{bidDeadline}
    and bid_date = #{bidDate}
    and winning_date = #{winningDate}
    and bid_opening_date = #{bidOpeningDate}
    and tender_agency = #{tenderAgency}
    and bidding_unit = #{biddingUnit}
    and quoted_price = #{quotedPrice}
    and construction_site =
        #{constructionSite}

    and bid_bond = #{bidBond}
    and win_bid = #{winBid}
    and bid_bond_recovered =
        #{bidBondRecovered}

    and bid_bond_takeback_date = #{bidBondTakebackDate}
    and
        bid_bond_reason_for_failure = #{bidBondReasonForFailure}

    and perform_bond = #{performBond}
    and perform_bond_recovered =
        #{performBondRecovered}

    and perform_bond_takeback_date = #{performBondTakebackDate}
    and
        perform_bond_reason_for_failure = #{performBondReasonForFailure}

    and bid_attachment = #{bidAttachment}
    and instance_id = #{instanceId}
    and status = #{status}
    and apply_status = #{applyStatus}
    and apply_time = #{applyTime}
    and project_id = #{projectId}
    and project_category = #{projectCategory}
    and project_manager = #{projectManager}
    and project_manager_name like
        concat('%', #{projectManagerName}, '%')

    and project_manager_contact =
        #{projectManagerContact}

    and agent = #{agent}
    and agent_name like concat('%', #{agentName}, '%')
    and agent_contact = #{agentContact}
    and project_type = #{projectType}
    and scale = #{scale}
    and business_category =
        #{businessCategory}

    and union_company_name like concat('%',
        #{unionCompanyName}, '%')

    and bid_place = #{bidPlace}

select distinct bid_place
from pl_bid_info

    del_flag=#{delFlag}
